INSERT INTO "movies" VALUES (153410,'tt0213035','Kiss','Kiss (1963) is an experimental film directed by Andy Warhol, which runs 50 minutes and features various couples -- man and woman, woman and woman, man and man -- kissing for 3½ minutes each. The film features Naomi Levine, Gerard Malanga, Rufus Collins, and Ed Sanders. Kiss was followed by Eat (1963), Sleep (1963), and Blow Job (1964). This was one of the first films Warhol made at The Factory in New York City.','[]','1963-12-31',0,0,55.0,NULL,'[{"id": 99, "name": "Documentary"}]','Released');
