INSERT INTO "movies" VALUES (170368,'tt0057642','The Green Years','Julio, aged nineteen, has just left the provinces to settle down in the outskirts of Lisbon. He lives there in a poor area with his uncle Raul and starts working as an apprentice shoemaker. At the shop, he gets to know Ilda, a young housemaid and regular customer. Ilda is pretty, joyful and modern and Julio falls for her.','[{"name": "Produções Cunha Telles", "id": 15448}]','1963-11-29',0,0,91.0,NULL,'[{"id": 18, "name": "Drama"}]','Released');
INSERT INTO "movies" VALUES (182099,'tt0222664','21-87','"21-87 is a 1963 Canadian abstract film created by Arthur Lipsett that lasts 9 minutes and 33 seconds.  The short film, produced by the National Film Board of Canada, is a collage of snippets from discarded footage found by Lipsett in the editing room of the National Film Board (where he was employed as an animator), combined with his own black and white 16mm footage which he shot on the streets of Montreal and New York City, among other locations."','[{"name": "National Film Board of Canada", "id": 10473}]','1963-04-24',0,0,9.0,NULL,'[{"id": 99, "name": "Documentary"}]','Released');
